import './App.css';
import Navbar from './components/Navbar';
import { TextForm } from './components/TextForm';
import React,{useState} from 'react'
import Alert from './components/Alert';

function App() {
  const [darkMode,setDarkMode] = useState('light');
  const [alert,setAlert] = useState(null);
  const [chgTheme,setTheme] = useState('light');

  const showAlert = (message,type) =>{
      setAlert({
          msg:message,
          type:type
      });

      setTimeout (()=>{
                        setAlert(null);
                      },3000);
  }



  const enableDarkMode = () =>{

    if(darkMode==="light"){
      setDarkMode('dark');
      document.body.style.backgroundColor="#042743";
      showAlert('Dark Mode has enabled','success');
    }else{
      setDarkMode('light');
      document.body.style.backgroundColor="white";
      showAlert('Light Mode has enabled','success');
    }
  }

  const ChangeTheme = () =>{
      if(chgTheme==="light"){
        setTheme('green');
        document.body.style.backgroundColor="#374151";
        //showAlert('Dark Mode has enabled','success');
      }else{
        setTheme('light');
        document.body.style.backgroundColor="white";
        //showAlert('Light Mode has enabled','success');
      }
  }




  return (
            <>
              <div className="clas"></div>
              <Navbar title="TextUtile" mode={darkMode} ChangeColor={ChangeTheme} toggleMode={enableDarkMode}/>
              <Alert alert={alert}/>
              <div className='container my-3'>
               <TextForm showAlert={showAlert} heading="enter the text" theme={chgTheme} mode={darkMode}  />
              </div>
            </>
         );
}

export default App;
